
public class CharaterToAscii {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char ch = 's';
		int ascii=ch;
		int castAscii=(int)ch;
		System.out.println("the actual character is "+ch);
		System.out.println("the ascii value of the character is "+ascii);
		System.out.println("the ascii value of the character is "+castAscii);
	}

}
